import React , {Component} from 'react';

export default class CommentListItem  {
  render() {
    const { inputs, id } = this.props;
    return
        (
          <ul>
         {
           inputs.map( (input ) => {
             return(
             <li
                key={id}>
               {inputs.author}
               {inputs.commentText}
            </li>
          )})
        }
        </ul>
    )
  };
}
