import React from 'react';

import AddCommentForm from '../add-comment-form/add-comment-form'

const CommentList = ({ fields, id }) => {
  const elements = fields.map((field) => {
    return (
      <ul key={id}>
        <li>{field.author}</li>
        <li>{field.commentText}</li>
        <li>{field.date}</li>

      </ul>
    )}
  )
  return (
    <ul>
      { elements }
    </ul>
  );
};


export default CommentList;
