import React from 'react';

import AddCommentForm from '../add-comment-form/add-comment-form'

const CommentList = ({ inputs, id }) => {
  const elements = inputs.map((input) => {
    return (
      <ul key={id}>
        <li>{input.author}</li>
        <li>{input.commentText}</li>
        <li>{input.date}</li>
      />
      </ul>
    )}
  )
  return (
    <ul>
      { elements }
    </ul>
  );
};


export default CommentList;
