import React from 'react';

import CommentListItem from '../comment-list-item/comment-list-item'

const CommentList = ({ inputs, id }) => {
  const elements = inputs.map((input) => {
    return (
      <li key={id}>
      <CommentListItem
        author={input.author}
        commentText={input.commentText}
        date={input.date}
      />
      </li>
    )}
  )
  return (
    <ul>
      { elements }
    </ul>
  );
};


export default CommentList;
